import React, { Component } from 'react';
import Cardlist from './Cardlist';
import SearchBox from './SearchBox';
import { robots } from './robofriends';

class App extends Component {
	constructor() {
		super()
		this.state = {
			robot: robots,
			searchfield: ''
		}
	}
	render() {
		return (
			<div className='tc'>
				<h1>RoboFriends</h1>
				<SearchBox />
				<Cardlist robots={this.state.robots} />
			</div>

		);
	}
}

export default App;
